mapper.sql:
WHERE DEL_DT IS NULL
	 AND CD_CLS_ID = #{cdClsId} 
	 AND REC_ST = #{recSt}

mapper.insert:
INSERT INTO  (
	CD_ID, CD_CLS_ID, CD_NM, CD_DESC, REC_ST, REG_ID, REG_DT
) VALUES (
	#{cdId}, #{cdClsId}, #{cdNm}, #{cdDesc,jdbcType=VARCHAR}, #{recSt}, #{regId}, SYSDATE()
)

mapper.update:
UPDATE 
SET 
	, CD_NM = #{cdNm}
	, CD_DESC = #{cdDesc,jdbcType=VARCHAR}
	, REC_ST =  #{recSt}
WHERE
	CD_ID = #{cdId,jdbcType=VARCHAR} and 
	DEL_DT IS NULL

mapper.delete:
DELETE FROM 
WHERE CD_ID = #{cdId}
	AND CD_CLS_ID = #{cdClsId}
