mapper.select:
select id, dat_st_id, crt_dt, mdfcn_dt, oper_co, oper_co_crt_dt, oper_co_mdfcn_dt, sexd, sexd_crt_dt, sexd_mdfcn_dt, brdt, brdt_crt_dt, brdt_mdfcn_dt, acdnt_ocrn_dt, acdnt_ocrn_dt_crt_dt, acdnt_ocrn_dt_mdfcn_dt, acdnt_ocrn_plc, acdnt_ocrn_plc_crt_dt, acdnt_ocrn_plc_mdfcn_dt, acdnt_type, acdnt_type_crt_dt, acdnt_type_mdfcn_dt, acdnt_cn, acdnt_cn_crt_dt, acdnt_cn_mdfcn_dt from datamart.acdnt_rcpt_info
crt_dt >= #{crtDt}
order by crt_dt
